// client/src/components/Login.js
import React, { useState } from 'react';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Email:', email);
    console.log('Password:', password);
    // You can later add login API call here
  };

  return (
    <div style={{
      display: 'flex',
      height: '100vh',
      alignItems: 'center',
      justifyContent: 'center',
      background: 'linear-gradient(to right, #8EC5FC, #E0C3FC)',
    }}>
      <form 
        onSubmit={handleSubmit}
        style={{
          backgroundColor: 'white',
          padding: '40px',
          borderRadius: '12px',
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
          width: '300px',
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        <h2 style={{ textAlign: 'center', marginBottom: '20px', color: '#4B0082' }}>
          Welcome Back ðŸ‘‹
        </h2>

        <label style={{ marginBottom: '5px', fontWeight: 'bold' }}>Email:</label>
        <input 
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter your email"
          style={{
            padding: '10px',
            marginBottom: '15px',
            borderRadius: '8px',
            border: '1px solid #ccc',
          }}
          required
        />

        <label style={{ marginBottom: '5px', fontWeight: 'bold' }}>Password:</label>
        <input 
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter your password"
          style={{
            padding: '10px',
            marginBottom: '20px',
            borderRadius: '8px',
            border: '1px solid #ccc',
          }}
          required
        />

        <button 
          type="submit"
          style={{
            padding: '10px',
            backgroundColor: '#6a11cb',
            backgroundImage: 'linear-gradient(to right, #2575fc, #6a11cb)',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            fontWeight: 'bold',
            cursor: 'pointer',
            transition: 'background 0.3s',
          }}
          onMouseOver={(e) => e.target.style.backgroundImage = 'linear-gradient(to right, #6a11cb, #2575fc)'}
          onMouseOut={(e) => e.target.style.backgroundImage = 'linear-gradient(to right, #2575fc, #6a11cb)'}
        >
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
