// client/src/App.js
import React from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import TaskList from './components/TaskList';

export default function App() {
  const isLoggedIn = !!localStorage.getItem('token');

  return (
    <Router>
      <Routes>
        <Route
          path="/login"
          element={isLoggedIn ? <Navigate to="/tasks" replace /> : <Login />}
        />
        <Route
          path="/register"
          element={isLoggedIn ? <Navigate to="/tasks" replace /> : <Register />}
        />
        <Route
          path="/tasks"
          element={isLoggedIn ? <TaskList /> : <Navigate to="/login" replace />}
        />
        {/* catch-all: redirect to tasks or login */}
        <Route
          path="*"
          element={<Navigate to={isLoggedIn ? "/tasks" : "/login"} replace />}
        />
      </Routes>
    </Router>
  );
}
