// client/src/components/Login.js
import React, { useState } from 'react';
import { motion } from 'framer-motion';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Email:', email);
    console.log('Password:', password);
    // Add login API call later
  };

  const togglePasswordVisibility = () => {
    setShowPassword(prevState => !prevState);
  };

  return (
    <div style={{
      display: 'flex',
      height: '100vh',
      alignItems: 'center',
      justifyContent: 'center',
      background: 'linear-gradient(to right, #8EC5FC, #E0C3FC)',
    }}>
      <motion.form 
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.7, type: 'spring' }}
        onSubmit={handleSubmit}
        style={{
          backgroundColor: 'white',
          padding: '40px',
          borderRadius: '12px',
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
          width: '320px',
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        <h2 style={{ textAlign: 'center', marginBottom: '20px', color: '#4B0082' }}>
          Welcome Back ðŸ‘‹
        </h2>

        <label style={{ marginBottom: '5px', fontWeight: 'bold' }}>Email:</label>
        <input 
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Enter your email"
          style={{
            padding: '10px',
            marginBottom: '15px',
            borderRadius: '8px',
            border: '1px solid #ccc',
          }}
          required
        />

        <label style={{ marginBottom: '5px', fontWeight: 'bold' }}>Password:</label>
        <div style={{ position: 'relative' }}>
          <input 
            type={showPassword ? 'text' : 'password'}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
            style={{
              padding: '10px',
              marginBottom: '15px',
              borderRadius: '8px',
              border: '1px solid #ccc',
              width: '100%',
            }}
            required
          />
          <span 
            onClick={togglePasswordVisibility}
            style={{
              position: 'absolute',
              right: '10px',
              top: '50%',
              transform: 'translateY(-50%)',
              cursor: 'pointer',
              fontSize: '14px',
              color: '#555',
            }}
          >
            {showPassword ? 'Hide' : 'Show'}
          </span>
        </div>

        <button 
          type="submit"
          style={{
            padding: '10px',
            backgroundColor: '#6a11cb',
            backgroundImage: 'linear-gradient(to right, #2575fc, #6a11cb)',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            fontWeight: 'bold',
            cursor: 'pointer',
            marginBottom: '10px',
            transition: 'background 0.3s',
          }}
          onMouseOver={(e) => e.target.style.backgroundImage = 'linear-gradient(to right, #6a11cb, #2575fc)'}
          onMouseOut={(e) => e.target.style.backgroundImage = 'linear-gradient(to right, #2575fc, #6a11cb)'}
        >
          Login
        </button>

        <div style={{ textAlign: 'center', marginTop: '10px' }}>
          <a href="#" style={{ fontSize: '14px', color: '#6a11cb', textDecoration: 'none' }}>
            Forgot Password?
          </a>
        </div>
      </motion.form>
    </div>
  );
};

export default Login;
